import styled from "styled-components";
import { colors } from "../../../components/common/elements/elements";

export const Styles = styled.div`
    .programme-area {
        padding: 65px 0;
        background: url(assets/images/bg-girl.png);
    background-repeat: no-repeat;
    background-position: bottom right;
    background-size: 36%;

        .programme_card {
            background: #fff;
            padding: 10px;
            box-shadow: 0px 0px 5px 0px rgb(0 0 0 / 10%);
            border-radius: 5px;
            margin-bottom:15px;
        }
        .school_name 
        {
            line-height: 1.2;
        span {
            color: #706d6d;
            font-size: 13px;
             font-weight: 500;
        }
        }
        .programme_detail
        {
            margin-top: 16px;
         h3 {
            color: #f63030;
            font-size: 18px;
            padding: 5px 0 0 0;
        }
        h5 {
            font-size: 14px;
            color: #f63030;
        }
        ul.programme_list li {
            display: block;
            line-height: 1.9;
            padding: 4px 0;
            border-bottom: 1px solid #e41a1a;
        }
        .featured_img 
        {
            position: relative;
            a {
                background: #f63030;
                padding: 8px 10px;
                color: #fff;
                font-size: 14px;
                font-weight: 500;
                border-radius: 3px;
                float: right;
                margin-bottom: 20px;
                position: absolute;
                right: 20px;
                bottom: 0px;
        }
        }
    }
    }

    .suContent h3 {
        font-size: 27px;
        font-weight: 600;
        letter-spacing: 0px;
        text-transform: none;
    }

`;
