import styled from "styled-components";
import { colors } from "../elements/elements";

export const Styles = styled.div`
.thinkLearning
{

.learningCard {
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
h3.subHeading {
    font-size: 30px;
    font-weight: 700;
    text-transform: uppercase;
    padding-bottom: 1rem;
    margin-bottom: 1rem;
    border-bottom: 1px solid #333;
}
}
`;
