import React, {useEffect} from 'react'
import Datas from '../data/icon-box/icon-box.json';
import { Container, Row, Col } from 'react-bootstrap';
import { Styles } from "./common/styles/iconBox";
import CountUp from 'react-countup';
import Aos from 'aos';
import 'aos/dist/aos.css';



const IconBox = () => {

    useEffect(() => {
        Aos.init({duration:2000});
       }, [])

    return (
        <>
            <Styles>
                {/* Icon Box */}
                <section className="icon-box-area pt-md-4 mt-2">
                    <Container>
                        <Row>
                            {
                                Datas.map((data, i) => (
                                    <Col  data-aos="fade-up" md="4" key={i}>
                                        <div className="full-icon-box">
                                            <div className="icon-box d-flex">
                                               
                                                <div data-aos="fade-right" className="box-title">
                                                    <h6><CountUp end={data.title} duration={3} delay={0.5} /></h6>
                                                    <p>{data.subTitle}</p>
                                                </div>
                                                <div data-aos="fade-right" className="smallBoxTtl">
                                                    <p> {data.showcase} </p>
                                                </div>
                                            </div>





                                        </div>
                                    </Col>
                                ))
                            }
                        </Row>
                    </Container>
                </section>
            </Styles>   
        </>
    )
}

export default IconBox
