import styled from "styled-components";
import { colors } from "../elements/elements";

export const Styles = styled.div`
    .programme-area {
        padding: 65px 0;
        
        .programme_card {
            background: #fff;
            padding: 10px;
            box-shadow: 0px 0px 5px 0px rgb(0 0 0 / 10%);
            border-radius: 5px;
        }
        .school_name 
        line-height: 1.2;
        {
        span {
            color: #706d6d;
            font-size: 13px;
            font-weight: 500;
        }
        }
        .programme_detail
        {

         h3 {
            color: #f63030;
            font-size: 18px;
            padding: 5px 0 0 0;
        }
        h5 {
            font-size: 14px;
            color: #f63030;
        }
    }
    }
`;
